import React, { useState } from 'react';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import './login.css';
import axios from 'axios';

const SignUpWithPhone = ({ onLoginSuccess }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [lastName, setLastName] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [otp, setOtp] = useState('');

  const handleSendOtp = async () => {
    // بررسی وجود شماره تلفن و نام خانوادگی
    if (phoneNumber && lastName) {
      try {
        // ارسال درخواست برای ارسال OTP
        const response = await axios.post('/api/send-otp', { phoneNumber, lastName });
        setOtpSent(true);
        console.log(response.data.message); // پیام موفقیت
      } catch (error) {
        console.error('Error sending OTP:', error);
        alert('خطا در ارسال کد تایید.');
      }
    } else {
      alert('لطفاً شماره تلفن و نام خانوادگی را وارد کنید.');
    }
  };

  const handleVerifyOtp = async () => {
    // بررسی وجود OTP
    if (otp) {
      try {
        // ارسال درخواست برای تأیید OTP
        const response = await axios.post('/api/verify-otp', { phoneNumber, otp });
        console.log(response.data.message); // پیام تایید موفقیت
        onLoginSuccess(); // تابع برای موفقیت در ورود
      } catch (error) {
        console.error('Error verifying OTP:', error);
        alert('کد یکبار مصرف نامعتبر است.');
      }
    } else {
      alert('لطفاً کد یکبار مصرف را وارد کنید.');
    }
  };

  return (
    <div className="signup-container">
      {!otpSent ? (
        <div className="form-box">
          <h2>ثبت نام</h2>
          <input
            type="text"
            placeholder="نام خانوادگی"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            required
          />
          <PhoneInput
            country={'ir'}
            value={phoneNumber}
            onChange={(value) => setPhoneNumber(value)}
            inputStyle={{
              width: '100%',
              borderRadius: '5px',
              borderColor: '#ccc',
              height: '40px',
            }}
          />
          <button onClick={handleSendOtp}>ادامه</button>
        </div>
      ) : (
        <div className="form-box">
          <h2>تایید کد یکبار مصرف</h2>
          <input
            type="text"
            placeholder="کد یکبار مصرف"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            required
          />
          <button onClick={handleVerifyOtp}>تایید</button>
        </div>
      )}
    </div>
  );
};

export default SignUpWithPhone;
